var import_obsidian2 = require("obsidian");
var FileService = class {
  constructor(app) {
    this.app = app;
  }
  async readFile(file2) {
    return this.app.vault.read(file2);
  }
  async getActiveFile() {
    return this.app.workspace.getActiveFile();
  }
  async createFile(filePath, content) {
    return this.app.vault.create(filePath, content);
  }
  async modifyFile(file2, newContent) {
    await this.app.vault.modify(file2, newContent);
  }
  async createFolder(folderPath) {
    if (!await this.app.vault.adapter.exists(folderPath)) {
      await this.app.vault.createFolder(folderPath);
    }
  }
  getAbstractFileByPath(path) {
    return this.app.vault.getAbstractFileByPath(path);
  }
  async findLatestFile(folderPath, prefix) {
    const folder = this.app.vault.getAbstractFileByPath(folderPath);
    if (!(folder instanceof import_obsidian2.TFolder)) {
      return null;
    }
    const files = folder.children.filter(
      (file2) => file2 instanceof import_obsidian2.TFile && file2.name.startsWith(prefix)
    );
    if (files.length === 0) {
      return null;
    }
    files.sort((a, b) => b.stat.ctime - a.stat.ctime);
    return files[0];
  }
};

